public class OneEditDistance {



    public boolean isOneEditDistance2(String s, String t) {
        if(s == null || t == null || s.equals("") && t.equals("") || Math.abs(s.length() - t.length()) > 1)
            return false;

        int misMatchCount = 0;
        for(int i = 0, j = 0; i < s.length() || j < t.length();) {
            if(i<s.length() && j < t.length()) {
                if(s.charAt(i) != t.charAt(j)) {
                    if(s.length() > t.length()) {
                        i++;
                        misMatchCount++;
                    } else if(s.length() < t.length()) {
                        j++;
                        misMatchCount++;
                    } else {
                        i++;
                        j++;
                        misMatchCount++;
                    }
                } else {
                    i++;
                    j++;
                }
            } else if(i<s.length()){
                i++;
                misMatchCount++;
            } else {
                j++;
                misMatchCount++;
            }

        }

        return misMatchCount == 1;
    }


    public boolean isOneEditDistance(String s, String t) {
        if(s == null || t == null || s.equals("") && t.equals(""))
            return false;

        char[] tArry = t.toCharArray();
        char[] sArry = s.toCharArray();

        //eucledean algo
        long[][] dp = new long[tArry.length+1][sArry.length+1];
        dp[0][0]=0;

        for(int i=1; i < dp[0].length; i++) {
            dp[0][i] = i;
        }
        for(int i=1; i < dp.length; i++) {
            dp[i][0] = i;
        }
        for(int i = 0; i<tArry.length; i++) {
            for(int j = 0; j <sArry.length; j++) {
                if(tArry[i] == sArry[j]) {
                    dp[i+1][j+1] = dp[i][j];
                } else {
                    dp[i+1][j+1] = dp[i][j] + Math.min(Math.abs(dp[i][j+1]), Math.abs(dp[i+1][j]));
                }
            }
        }

//        System.out.println(" X " + "t e a c h e r");
//        for(int i = 0; i<dp.length; i++) {
//            System.out.print(i==0?"X" : tArry[i-1]);
//            for(int j = 0; j <dp[0].length; j++) {
//                System.out.print(" " + dp[i][j]);
//            }
//            System.out.println();
//        }

//        Arrays.stream(dp[tArry.length]).forEach(a -> System.out.print(" " + a));
        return dp[tArry.length][sArry.length] == 1;
    }

    public static void main(String[] args) {
        OneEditDistance distance = new OneEditDistance();
//        System.out.println(distance.isOneEditDistance2("",
//                ""));
        System.out.println(distance.isOneEditDistance2("teacher","tebacher"));


    }
}
